export type Folder = {
    /** @description (Google only) Folder background color. */
    background_color?: string;
    /** @description (Microsoft only) The number of immediate child folders in the current folder. */
    child_count?: number;
    grant_id?: string;
    id?: string;
    /**
     * @description Folder name
     * @example Invoices
     */
    name?: string;
    object?: string;
    /** @description (Microsoft only) ID of the parent folder. */
    parent_id?: string;
    /** @description (Google only) Indicates if the folder is user created or system created. */
    system_folder?: boolean;
    /** @description (Google only) Folder text color. */
    text_color?: string;
    /** @description The number of items inside of a folder. */
    total_count?: number;
    /** @description The number of unread items inside of a folder. */
    unread_count?: number;
};
