import { Store } from "../@types";

class LocalStorage implements Store {
  public get(key: string): Promise<null | any> {
    return new Promise((res) => {
      const record = window.localStorage.getItem(key);
      if (!record) {
        res(null);
      } else {
        res(JSON.parse(record));
      }
    });
  }
  public async remove(key: string): Promise<null> {
    window.localStorage.removeItem(key);
    return new Promise((res) => {
      res(null);
    });
  }
  public set(key: string, value: any): Promise<null> {
    window.localStorage.setItem(key, JSON.stringify(value));
    return new Promise((res) => {
      res(null);
    });
  }
}
export default LocalStorage;
