import { BaseNylasConnectorInterface, NylasSchedulerConfigConnectorInterface, NylasSchedulerConnectorInterface } from "../connector/connector-interface";
import { NylasBookedEventCard } from '../components/scheduler/nylas-booked-event-card/nylas-booked-event-card';
import { NylasDatePicker } from '../components/scheduler/nylas-date-picker/nylas-date-picker';
import { NylasLocaleSwitch } from '../components/scheduler/nylas-locale-switch/nylas-locale-switch';
import { NylasBookingForm } from '../components/scheduler/nylas-booking-form/nylas-booking-form';
import { NylasSelectedEventCard } from '../components/scheduler/nylas-selected-event-card/nylas-selected-event-card';
import { NylasTimeslotPicker } from '../components/scheduler/nylas-timeslot-picker/nylas-timeslot-picker';
import { ExtractEventEmitterProperties } from './types';
import { NylasAvailabilityPicker } from '../components/scheduler-editor/nylas-availability-picker/nylas-availability-picker';
import { NylasCalendarPicker } from '../components/scheduler-editor/nylas-calendar-picker/nylas-calendar-picker';
import { NylasBookingCalendarPicker } from '../components/scheduler-editor/nylas-booking-calendar-picker/nylas-booking-calendar-picker';
import { NylasEventTitle } from '../components/scheduler-editor/nylas-event-title/nylas-event-title';
import { NylasEventDuration } from '../components/scheduler-editor/nylas-event-duration/nylas-event-duration';
import { NylasEventDescription } from '../components/scheduler-editor/nylas-event-description/nylas-event-description';
import { NylasEditorTabs } from '../components/scheduler-editor/nylas-editor-tabs/nylas-editor-tabs';
import { NylasListConfigurations } from '../components/scheduler-editor/nylas-list-configurations/nylas-list-configurations';
export type EventOverride<Connector extends BaseNylasConnectorInterface> = Record<string, (event: CustomEvent<any>, connector?: Connector) => Promise<void>>;
export type SchedulerComponents = NylasTimeslotPicker | NylasDatePicker | NylasLocaleSwitch | NylasBookingForm | NylasSelectedEventCard | NylasBookedEventCard;
export type NylasTimeslotPickerEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasTimeslotPicker>, undefined>;
export type NylasDatePickerEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasDatePicker>, undefined>;
export type NylasLocaleSwitchEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasLocaleSwitch>, undefined>;
export type NylasSchedulerAdditionalDataEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasBookingForm>, undefined>;
export type NylasSelectedEventCardEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasSelectedEventCard>, undefined>;
export type NylasBookedEventCardEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasBookedEventCard>, undefined>;
export type SchedulerEventEmitterProperties = NylasTimeslotPickerEventEmitterProperties | NylasDatePickerEventEmitterProperties | NylasLocaleSwitchEventEmitterProperties | NylasSchedulerAdditionalDataEventEmitterProperties | NylasSelectedEventCardEventEmitterProperties | NylasBookedEventCardEventEmitterProperties;
export type SchedulerEventOverride = Partial<{
    [P in SchedulerEventEmitterProperties as P['key']]: (event: CustomEvent<P['eventType']>, connector?: NylasSchedulerConnectorInterface) => Promise<void>;
}> & EventOverride<NylasSchedulerConnectorInterface>;
export type NylasSchedulerEditorComponents = NylasAvailabilityPicker | NylasCalendarPicker | NylasBookingCalendarPicker | NylasEventTitle | NylasEventDuration | NylasEventDescription | NylasEditorTabs;
export type NylasAvailabilityPickerEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasAvailabilityPicker>, undefined>;
export type NylasCalendarPickerEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasCalendarPicker>, undefined>;
export type NylasBookingCalendarPickerEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasBookingCalendarPicker>, undefined>;
export type NylasEventTitleEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasEventTitle>, undefined>;
export type NylasEventDurationEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasEventDuration>, undefined>;
export type NylasEventDescriptionEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasEventDescription>, undefined>;
export type NylasEditorTabsEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasEditorTabs>, undefined>;
export type NylasListConfigurationsEventEmitterProperties = Exclude<ExtractEventEmitterProperties<NylasListConfigurations>, undefined>;
export type NylasSchedulerEditorEventEmitterProperties = NylasAvailabilityPickerEventEmitterProperties | NylasCalendarPickerEventEmitterProperties | NylasBookingCalendarPickerEventEmitterProperties | NylasEventTitleEventEmitterProperties | NylasEventDurationEventEmitterProperties | NylasEventDescriptionEventEmitterProperties | NylasEditorTabsEventEmitterProperties | NylasListConfigurationsEventEmitterProperties;
export type SchedulerEditorEventOverride = Partial<{
    [P in NylasSchedulerEditorEventEmitterProperties as P['key']]: (event: CustomEvent<P['eventType']>, connector?: NylasSchedulerConnectorInterface) => Promise<void>;
}> & EventOverride<NylasSchedulerConfigConnectorInterface>;
